import React, { useState } from 'react';
import '../css/NavigationTop.css';

// Top Navigation Bar

function NavigationTop() {
    const [language, setLanguage] = useState('EN');
    const [isDropdownVisible, setDropdownVisible] = useState(false);

    const signIn = () => {
        console.log("Sign in clicked");
    };

    const signOut = (event) => {
        event.stopPropagation();
        console.log("Sign out clicked");
    };

    const toggleDropdown = (event) => {
        event.stopPropagation();
        setDropdownVisible(!isDropdownVisible);
    };

    const updateLanguage = (newLanguage, event) => {
        event.stopPropagation();
        setLanguage(newLanguage);
    };

    return (
        <div className="navigation-top-container">
            <div className="navigation-top">
                <div className="navigation-top-left">
                    <a href="/home">Home</a>
                    <a href="/about">About</a>
                    <a href="/contact">Contact</a>
                </div>
                <div className="navigation-top-right">
                    <a href="/signin" onClick={signIn}>Sign In</a>
                    <div className="dropdown">
                        <button className="dropbtn" onClick={toggleDropdown}>
                            {language}
                        </button>
                        {isDropdownVisible &&
                            <div className="dropdown-content">
                                <a href="/en" onClick={(event) => updateLanguage('EN', event)}>EN</a>
                                <a href="/es" onClick={(event) => updateLanguage('ES', event)}>ES</a>
                                <a href="/fr" onClick={(event) => updateLanguage('FR', event)}>FR</a>
                            </div>
                        }
                    </div>
                    <a href="/signout" onClick={signOut}>Sign Out</a>
                </div>
            </div>
        </div>
    );
}

export default NavigationTop;
