            {/*    <div className="search-container">*/}
            {/*        <input*/}
            {/*            type="text"*/}
            {/*            id="search-input"*/}
            {/*            placeholder="Search..."*/}
            {/*            value={query}*/}
            {/*            onChange={handleInputChange}*/}
            {/*        />*/}
            {/*        <button*/}
            {/*            type="button"*/}
            {/*            id="search-button"*/}
            {/*            onClick={handleSearch}*/}
            {/*        />*/}
            {/*    </div>*/}

            {/*    /!* login *!/*/}
            {/*    /!*<div className="login-container" role="button" tabIndex="0">*!/*/}
            {/*    /!*    /!*<div className="sign-icon">*!/*!/*/}
            {/*    /!*    /!*    <i className="fa fa-user-circle-o" style="font-size:30px"></i>*!/*!/*/}
            {/*    /!*    /!*</div>*!/*!/*/}
            {/*    /!*    /!*<div className="sign-text">Sign In</div>*!/*!/*/}
            {/*    /!*</div>*!/*/}


            {/*</div>*/}

            {/*<div className="buttom-bar">*/}

            {/*</div>*/}

            {/*<div className="logo">*/}
